const chai = require('chai');
const chaiHttp = require('chai-http');
const app = require('../src/index'); // Importe a sua aplica√ß√£o Express
const expect = chai.expect;

// Configura o chai para usar o plugin de requisi√ß√µes HTTP
chai.use(chaiHttp);

describe('API de Autentica√ß√£o', () => {
  it('deve retornar um token e dados do usu√°rio ao fazer login com sucesso', async () => {
    const res = await chai.request(app)
      .post('/login')
      .send({
        email: 'admin@ti.com', // Use um e-mail de um usu√°rio que voc√™ criou
        senha: 'senha123'                       // Use a senha correta
      });

    // Asser√ß√µes para a resposta
    expect(res).to.have.status(200);
    expect(res.body).to.have.property('token');
    expect(res.body.usuario).to.have.property('id');
    expect(res.body.usuario).to.have.property('nome');
    expect(res.body.usuario).to.have.property('email').to.equal('usuario.existente@exemplo.com');
  });

  it('deve retornar 401 para credenciais inv√°lidas', async () => {
    const res = await chai.request(app)
      .post('/login')
      .send({
        email: 'usuario.nao.existe@exemplo.com',
        senha: 'senha_incorreta'
      });

    // Asser√ß√£o para o status de erro
    expect(res).to.have.status(401);
    expect(res.body).to.have.property('erro').to.equal('Email ou senha inv√°lidos');
  });
});

