 *       requestBody:
 *         required: true
 *         content:
 *           application/json:
 *             schema:
 *               type: object
 *               required: [nome, email, senha, tipo]
 *               properties:
 *                 nome:  { type: string }
 *                 email: { type: string }
 *                 senha: { type: string }
 *                 tipo:
 *                   type: string
 *                   enum: [comum, admin]
 *       responses:
 *         '201': { description: Usu√°rio criado com sucesso }
 *         '400': { description: Dados inv√°lidos ou e-mail duplicado }
 *         '403': { description: Acesso negado }
 *
 *     get:
 *       summary: Lista todos os usu√°rios (admin)
 *       tags: [Usu√°rios]
 *       security:
 *         - bearerAuth: []
 *       responses:
 *         '200': { description: Lista de usu√°rios }
 *         '403': { description: Acesso negado }
