 *   /usuarios/{id}:
 *     parameters:
 *       - in: path
 *         name: id
 *         required: true
 *         schema: { type: integer }
 *
 *     get:
 *       summary: Obt√©m um usu√°rio por ID (admin)
 *       tags: [Usu√°rios]
 *       security:
 *         - bearerAuth: []
 *       responses:
 *         '200': { description: Dados do usu√°rio }
 *         '403': { description: Acesso negado }
 *         '404': { description: Usu√°rio n√£o encontrado }
 *
 *     put:
 *       summary: Atualiza um usu√°rio existente (admin)
