 *
 *     delete:
 *       summary: Deleta um usu√°rio por ID (admin)
 *       tags: [Usu√°rios]
 *       security:
 *         - bearerAuth: []
 *       responses:
 *         '204': { description: Usu√°rio deletado com sucesso }
 *         '403': { description: Acesso negado }
 *         '404': { description: Usu√°rio n√£o encontrado }
 *
 *   /usuarios/{id}/chamados:
 *     get:
 *       summary: Lista todos os chamados abertos por um usu√°rio (admin)
 *       tags: [Usu√°rios]
 *       security:
 *         - bearerAuth: []
 *       parameters:
 *         - in: path
 *           name: id
